import React, { useState } from 'react'
import styles from './productCard.module.scss'

const ProductCard = ({ product, onAddToCart }) => {
	const [quantity, setQuantity] = useState(1)

	const handleIncrement = () => {
		setQuantity(prev => prev + 1)
	}

	const handleDecrement = () => {
		if (quantity > 1) {
			setQuantity(prev => prev - 1)
		}
	}

	const handleAddToCart = () => {
		onAddToCart(product, quantity)
		setQuantity(1)
	}

	return (
		<div className={styles.card}>
			<div className={styles.imageContainer}>
				<img
					src={
						product.thumbnail ||
						'https://cs13.pikabu.ru/post_img/big/2023/03/20/9/1679327108295817795.jpg'
					}
					alt={product.name}
					className={styles.thumbnail}
				/>
			</div>

			<div className={styles.cardHeader}>
				<h3 className={styles.productName}>{product.name}</h3>
				<span className={styles.price}>{product.price} руб</span>
			</div>

			<div className={styles.quantityControls}>
				<button
					className={styles.quantityBtn}
					onClick={handleDecrement}
					disabled={quantity <= 1}
				>
					-
				</button>
				<span className={styles.quantity}>{quantity} шт</span>
				<button className={styles.quantityBtn} onClick={handleIncrement}>
					+
				</button>
			</div>

			<button className={styles.addToCartBtn} onClick={handleAddToCart}>
				В корзину
			</button>

			<div className={styles.total}>
				<span>{quantity} шт</span>
				<span>{product.price * quantity} руб</span>
			</div>
		</div>
	)
}

export default ProductCard
