import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import PickupModal from './PickupModal'
import DeliveryModal from './DeliveryModal'
import Notification from './/Notification'
import s from './cartPage.module.scss'

const CartPage = ({
	cartItems = [],
	onUpdateQuantity,
	onRemoveItem,
	onClearCart,
}) => {
	const [showPickupModal, setShowPickupModal] = useState(false)
	const [showDeliveryModal, setShowDeliveryModal] = useState(false)
	const [selectedPickupPoint, setSelectedPickupPoint] = useState(null)
	const [notification, setNotification] = useState({
		show: false,
		message: '',
		type: '',
	})

	const subtotal = cartItems.reduce(
		(sum, item) => sum + item.price * item.quantity,
		0
	)
	const total = subtotal

	const pickupPoints = [
		{
			id: 1,
			name: '–†–¶ –ù–õ–û',
			address: '—É–ª. –Ø–∫—É–±–∞ –ö–æ–ª—è—Å–∞, 37',
			hours: '–ö—Ä—É–≥–ª–æ—Å—É—Ç–æ—á–Ω–æ',
			deliveryTime: '–°—É—à–∏ - 15 –º–∏–Ω, WOK - 15 –º–∏–Ω',
		},
		// ... –æ—Å—Ç–∞–ª—å–Ω—ã–µ —Ç–æ—á–∫–∏
	]

	const showNotification = (message, type = 'success') => {
		setNotification({ show: true, message, type })
		setTimeout(
			() => setNotification({ show: false, message: '', type: '' }),
			3000
		)
	}

	const handlePickupOrder = async pickupPoint => {
		try {
			// –û—Ç–ø—Ä–∞–≤–∫–∞ –∑–∞–∫–∞–∑–∞ –Ω–∞ mockAPI
			const orderData = {
				type: 'pickup',
				items: cartItems,
				pickupPoint,
				total,
				status: 'confirmed',
				date: new Date().toISOString(),
			}

			const response = await fetch('https://your-mockapi.io/orders', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
				},
				body: JSON.stringify(orderData),
			})

			if (response.ok) {
				showNotification(
					'–ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω —É—Å–ø–µ—à–Ω–æ! –û–∂–∏–¥–∞–π—Ç–µ —Å–º—Å —Å –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ–º.',
					'success'
				)
				onClearCart() // –û—á–∏—â–∞–µ–º –∫–æ—Ä–∑–∏–Ω—É –ø–æ—Å–ª–µ —É—Å–ø–µ—à–Ω–æ–≥–æ –∑–∞–∫–∞–∑–∞
			} else {
				throw new Error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏–∏ –∑–∞–∫–∞–∑–∞')
			}
		} catch (error) {
			showNotification(
				'–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏–∏ –∑–∞–∫–∞–∑–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.',
				'error'
			)
		}

		setShowPickupModal(false)
	}

	const handleDeliveryOrder = async deliveryData => {
		try {
			const orderData = {
				type: 'delivery',
				items: cartItems,
				deliveryData,
				total,
				status: 'confirmed',
				date: new Date().toISOString(),
			}

			const response = await fetch('https://your-mockapi.io/orders', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
				},
				body: JSON.stringify(orderData),
			})

			if (response.ok) {
				showNotification(
					'–ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω —É—Å–ø–µ—à–Ω–æ! –ö—É—Ä—å–µ—Ä —Å–≤—è–∂–µ—Ç—Å—è —Å –≤–∞–º–∏.',
					'success'
				)
				onClearCart()
			} else {
				throw new Error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏–∏ –∑–∞–∫–∞–∑–∞')
			}
		} catch (error) {
			showNotification(
				'–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏–∏ –∑–∞–∫–∞–∑–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.',
				'error'
			)
		}

		setShowDeliveryModal(false)
	}

	return (
		<div className={s.cartPage}>
			<div className={s.container}>
				<h1 className={s.title}>–ö–æ—Ä–∑–∏–Ω–∞</h1>

				{cartItems.length === 0 ? (
					<div className={s.emptyCart}>
						<div className={s.emptyCartIcon}>üõí</div>
						<h2>–í–∞—à–∞ –∫–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞</h2>
						<p>–î–æ–±–∞–≤—å—Ç–µ —Ç–æ–≤–∞—Ä—ã –∏–∑ –º–µ–Ω—é, —á—Ç–æ–±—ã —Å–¥–µ–ª–∞—Ç—å –∑–∞–∫–∞–∑</p>
						<Link to='/' className={s.continueShopping}>
							–ü–µ—Ä–µ–π—Ç–∏ –∫ –º–µ–Ω—é
						</Link>
					</div>
				) : (
					<div className={s.cartContent}>
						<div className={s.itemsList}>
							{cartItems.map(item => (
								<div key={item.id} className={s.cartItem}>
									{/* ... –æ—Å—Ç–∞–ª—å–Ω–æ–π –∫–æ–¥ –∫–∞—Ä—Ç–æ—á–∫–∏ —Ç–æ–≤–∞—Ä–∞ */}
								</div>
							))}
						</div>

						<div className={s.orderSummary}>
							{/* ... –±–ª–æ–∫ —Å —Å—É–º–º–æ–π –∑–∞–∫–∞–∑–∞ */}

							<div className={s.orderButtons}>
								<button
									className={s.pickupBtn}
									onClick={() => setShowPickupModal(true)}
								>
									–°–∞–º–æ–≤—ã–≤–æ–∑
								</button>
								<button
									className={s.deliveryBtn}
									onClick={() => setShowDeliveryModal(true)}
								>
									–î–æ—Å—Ç–∞–≤–∫–∞
								</button>
							</div>

							<Link to='/' className={s.continueShopping}>
								‚Üê –í–µ—Ä–Ω—É—Ç—å—Å—è –∫ –ø–æ–∫—É–ø–∫–∞–º
							</Link>
						</div>
					</div>
				)}

				{/* –ú–æ–¥–∞–ª—å–Ω—ã–µ –æ–∫–Ω–∞ */}
				{showPickupModal && (
					<PickupModal
						pickupPoints={pickupPoints}
						selectedPoint={selectedPickupPoint}
						onSelectPoint={setSelectedPickupPoint}
						onOrder={handlePickupOrder}
						onClose={() => setShowPickupModal(false)}
					/>
				)}

				{showDeliveryModal && (
					<DeliveryModal
						onOrder={handleDeliveryOrder}
						onClose={() => setShowDeliveryModal(false)}
					/>
				)}

				{/* –£–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ */}
				<Notification
					show={notification.show}
					message={notification.message}
					type={notification.type}
					onClose={() =>
						setNotification({ show: false, message: '', type: '' })
					}
				/>
			</div>
		</div>
	)
}

export default CartPage
