	const [loading, setLoading] = useState(true)
	const [error, setError] = useState(null)
	const [activeFilter, setActiveFilter] = useState('все')

	const filters = ['все', 'супы', 'основные', 'карри', 'закуски']

	useEffect(() => {
		fetchProducts()
