import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import s from './header.module.scss'
import logo from '../assets/Logo.png'
import searchIcon from '../assets/bx_bx-search-alt-2.svg'
import cartIcon from '../assets/Shopping cart.svg'
import phoneIcon from '../assets/phone.svg'

function Header() {
	const [isMenuOpen, setIsMenuOpen] = useState(false)
	const [isScrolled, setIsScrolled] = useState(false)

	// Закрытие меню при изменении размера окна
	useEffect(() => {
		const handleResize = () => {
			if (window.innerWidth > 768) {
				setIsMenuOpen(false)
			}
		}

		// Эффект прозрачности при скролле
		const handleScroll = () => {
			setIsScrolled(window.scrollY > 50)
		}

		window.addEventListener('resize', handleResize)
		window.addEventListener('scroll', handleScroll)

		return () => {
			window.removeEventListener('resize', handleResize)
			window.removeEventListener('scroll', handleScroll)
		}
	}, [])

	// Закрытие меню при клике на ссылку
	const handleLinkClick = () => {
		setIsMenuOpen(false)
	}

	// Закрытие меню при клике вне его области
	useEffect(() => {
		const handleClickOutside = event => {
			if (
				isMenuOpen &&
				!event.target.closest(`.${s.menu}`) &&
				!event.target.closest(`.${s.burger}`)
			) {
				setIsMenuOpen(false)
			}
		}

		document.addEventListener('mousedown', handleClickOutside)
		return () => document.removeEventListener('mousedown', handleClickOutside)
	}, [isMenuOpen])

	return (
		<header className={`${s.header} ${isScrolled ? s.scrolled : ''}`}>
			{/* Логотип */}
			<Link
				className={s.logo}
				to='/'
				onClick={handleLinkClick}
				aria-label='На главную страницу'
			>
				<img src={logo} alt='Foodies Logo' />
			</Link>

			{/* Поиск */}
			<div className={s.search}>
				<img src={searchIcon} alt='Поиск' />
				<input
					type='text'
					name='search'
					id='search'
					placeholder='Поиск'
					aria-label='Поиск по сайту'
				/>
			</div>

			{/* Бургер меню */}
			<button
				className={`${s.burger} ${isMenuOpen ? s.active : ''}`}
				onClick={() => setIsMenuOpen(!isMenuOpen)}
				aria-label={isMenuOpen ? 'Закрыть меню' : 'Открыть меню'}
				aria-expanded={isMenuOpen}
			>
				<span></span>
				<span></span>
				<span></span>
			</button>

			{/* Основное меню */}
			<nav
				className={`${s.menu} ${isMenuOpen ? s.active : ''}`}
				aria-hidden={!isMenuOpen}
			>
				<Link className={s.cart} to='/cart' onClick={handleLinkClick}>
					<img src={cartIcon} alt='Корзина' />
					{cartItemsCount > 0 && (
						<span className={s.cartBadge}>{cartItemsCount}</span>
					)}
				</Link>

				<Link to='/' onClick={handleLinkClick}>
					Меню
				</Link>

				<Link to='/profile' onClick={handleLinkClick}>
					Профиль
				</Link>
			</nav>

			{/* Телефон */}
			<div className={s.phone}>
				<img src={phoneIcon} alt='Телефон' />
				<div>+7 (919) 123-12-31</div>
			</div>
		</header>
	)
}

export default Header
