import React, { useState, useEffect } from 'react'
import ProductCard from './ProductCard'
import ProductFilter from './ProductFilter'
import styles from './productList.module.scss'

const ProductList = ({ onAddToCart, searchQuery }) => {
	const [products, setProducts] = useState([])
	const [filteredProducts, setFilteredProducts] = useState([])
	const [loading, setLoading] = useState(true)
	const [error, setError] = useState(null)
	const [activeFilter, setActiveFilter] = useState('все')

	const filters = ['все', 'супы', 'основные', 'карри', 'закуски']

	useEffect(() => {
		fetchProducts()
	}, [])

	// Фильтрация продуктов по поисковому запросу и категории
	useEffect(() => {
		let result = products

		// Применяем поиск
		if (searchQuery) {
			result = result.filter(product =>
				product.name.toLowerCase().includes(searchQuery.toLowerCase())
			)
		}

		// Применяем фильтр по категории
		if (activeFilter !== 'все') {
			result = result.filter(product => product.category === activeFilter)
		}

		setFilteredProducts(result)
	}, [products, searchQuery, activeFilter])

	const fetchProducts = async () => {
		try {
			// Если mockAPI не работает, используем заглушку
			const mockProducts = [
				{
					id: 1,
					name: 'Спринг роллы',
					price: 280,
					category: 'закуски',
					thumbnail:
						'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnD4Z-qc0M2axIeXHmvxMhWXnGkANnRvqTmA&s',
				},
				{
					id: 2,
					name: 'Салат с папайей',
					price: 260,
					category: 'салаты',
					thumbnail:
						'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQY8-RhiuKOXPECJTbZuE_YRYeG67ioexOL9w&s',
				},
				{
					id: 3,
					name: 'Сет роллов',
					price: 720,
					category: 'сеты',
					thumbnail:
						'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRA1Z1QYIA9ddG2y9CahvPJLm1y8CRpkLP-NA&s',
				},
				{
					id: 4,
					name: 'Набор для компании',
					price: 1200,
					category: 'наборы',
					thumbnail:
						'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRDcyCIwcKerOU75FJH4ep4eWayr-TJ03QLxA&s',
				},
				{
					id: 5,
					name: 'Мини-сет закусок',
					price: 540,
					category: 'сеты',
					thumbnail:
						'https://deliveryfood.ru/pageimage/mini-zakuski-dlya-fursheta-foto.webp',
				},
			]

			setProducts(mockProducts)
			setFilteredProducts(mockProducts)

			// Раскомментировать для работы с реальным API
			/*
      const response = await fetch('https://68d662abc2a1754b426a8851.mockapi.io/products');
      if (!response.ok) throw new Error('Ошибка загрузки товаров');
      const data = await response.json();
      setProducts(data);
      setFilteredProducts(data);
      */
		} catch (err) {
			setError(err.message)
		} finally {
			setLoading(false)
		}
	}

	if (loading) return <div className={styles.loading}>Загрузка товаров...</div>
	if (error) return <div className={styles.error}>Ошибка: {error}</div>

	return (
		<div className={styles.container}>
			{searchQuery && (
				<div className={styles.searchInfo}>
					<h2>Результаты поиска: "{searchQuery}"</h2>
					<p>Найдено товаров: {filteredProducts.length}</p>
				</div>
			)}

			<ProductFilter
				filters={filters}
				activeFilter={activeFilter}
				onFilterChange={setActiveFilter}
			/>

			{filteredProducts.length === 0 ? (
				<div className={styles.noProducts}>
					<h3>Товары не найдены</h3>
					<p>
						Попробуйте изменить поисковый запрос или выбрать другую категорию
					</p>
				</div>
			) : (
				<div className={styles.productsGrid}>
					{filteredProducts.map(product => (
						<ProductCard
							key={product.id}
							product={product}
							onAddToCart={onAddToCart}
						/>
					))}
				</div>
			)}
		</div>
	)
}

export default ProductList
