import React, { useState, useEffect } from 'react'
import s from './profilePage.module.scss'

const ProfilePage = () => {
	const [userData, setUserData] = useState({
		name: '',
		email: '',
		phone: '',
		address: '',
	})
	const [orders, setOrders] = useState([])
	const [isLoading, setIsLoading] = useState(true)
	const [isEditing, setIsEditing] = useState(false)
	const [error, setError] = useState(null)

	// Mock данные на случай ошибки API
	const mockUserData = {
		id: '1',
		name: 'Иван Иванов',
		email: 'ivan@example.com',
		phone: '+79191234567',
		address: 'ул. Примерная, 123',
	}

	const mockOrders = [
		{
			id: '1',
			userId: '1',
			date: '2024-01-15T12:00:00.000Z',
			total: 1250,
			status: 'completed',
			type: 'delivery',
			items: [
				{ name: 'Том Ям', quantity: 2, price: 290 },
				{ name: 'Пад Тай', quantity: 1, price: 320 },
			],
			address: 'ул. Примерная, 123',
		},
		{
			id: '2',
			userId: '1',
			date: '2024-01-10T18:30:00.000Z',
			total: 890,
			status: 'delivered',
			type: 'pickup',
			items: [{ name: 'Спринг Роллы', quantity: 1, price: 280 }],
			pickupPoint: 'РЦ НЛО',
		},
	]

	useEffect(() => {
		fetchUserData()
		fetchOrders()
	}, [])

	const fetchUserData = async () => {
		try {
			const response = await fetch(
				'https://68d662abc2a1754b426a8851.mockapi.io/users/1'
			)
			if (!response.ok) {
				throw new Error('User not found')
			}
			const data = await response.json()
			setUserData(data)
		} catch (error) {
			console.error('Ошибка загрузки данных пользователя:', error)
			// Используем mock данные
			setUserData(mockUserData)
			setError('Используются демо-данные')
		} finally {
			setIsLoading(false)
		}
	}

	const fetchOrders = async () => {
		try {
			const response = await fetch(
				'https://68d662abc2a1754b426a8851.mockapi.io/orders?userId=1'
			)
			if (!response.ok) {
				throw new Error('Orders not found')
			}
			const data = await response.json()
			setOrders(Array.isArray(data) ? data : [])
		} catch (error) {
			console.error('Ошибка загрузки заказов:', error)
			// Используем mock данные
			setOrders(mockOrders)
		}
	}

	const handleSave = async () => {
		try {
			// Создаем или обновляем пользователя
			const response = await fetch(
				'https://68d662abc2a1754b426a8851.mockapi.io/users/1',
				{
					method: 'PUT',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify({
						...userData,
						id: '1',
						updatedAt: new Date().toISOString(),
					}),
				}
			)

			if (response.ok) {
				setIsEditing(false)
				alert('Данные успешно сохранены!')
			} else {
				throw new Error('Failed to save')
			}
		} catch (error) {
			console.error('Ошибка сохранения данных:', error)
			// Локальное сохранение в localStorage как fallback
			localStorage.setItem('userData', JSON.stringify(userData))
			setIsEditing(false)
			alert('Данные сохранены локально (API недоступно)')
		}
	}

	const handleInputChange = e => {
		setUserData({
			...userData,
			[e.target.name]: e.target.value,
		})
	}

	const getStatusText = status => {
		switch (status) {
			case 'completed':
				return 'Выполнен'
			case 'delivered':
				return 'Доставлен'
			case 'processing':
				return 'В обработке'
			case 'cooking':
				return 'Готовится'
			default:
				return status
		}
	}

	const formatDate = dateString => {
		return new Date(dateString).toLocaleDateString('ru-RU', {
			day: 'numeric',
			month: 'long',
			year: 'numeric',
			hour: '2-digit',
			minute: '2-digit',
		})
	}

	if (isLoading) {
		return <div className={s.loading}>Загрузка...</div>
	}

	return (
		<div className={s.profilePage}>
			<div className={s.container}>
				<h1 className={s.title}>Мой профиль</h1>

				{error && <div className={s.errorMessage}>{error}</div>}

				<div className={s.profileContent}>
					<div className={s.profileInfo}>
						<h2>Личная информация</h2>

						{!isEditing ? (
							<div className={s.infoDisplay}>
								<div className={s.infoItem}>
									<strong>Имя:</strong> {userData.name}
								</div>
								<div className={s.infoItem}>
									<strong>Email:</strong> {userData.email}
								</div>
								<div className={s.infoItem}>
									<strong>Телефон:</strong> {userData.phone}
								</div>
								<div className={s.infoItem}>
									<strong>Адрес:</strong> {userData.address}
								</div>
								<button
									className={s.editBtn}
									onClick={() => setIsEditing(true)}
								>
									Редактировать
								</button>
							</div>
						) : (
							<div className={s.infoEdit}>
								<div className={s.formGroup}>
									<label>Имя:</label>
									<input
										type='text'
										name='name'
										value={userData.name}
										onChange={handleInputChange}
										required
									/>
								</div>
								<div className={s.formGroup}>
									<label>Email:</label>
									<input
										type='email'
										name='email'
										value={userData.email}
										onChange={handleInputChange}
										required
									/>
								</div>
								<div className={s.formGroup}>
									<label>Телефон:</label>
									<input
										type='tel'
										name='phone'
										value={userData.phone}
										onChange={handleInputChange}
										required
									/>
								</div>
								<div className={s.formGroup}>
									<label>Адрес:</label>
									<input
										type='text'
										name='address'
										value={userData.address}
										onChange={handleInputChange}
									/>
								</div>
								<div className={s.editActions}>
									<button className={s.saveBtn} onClick={handleSave}>
										Сохранить
									</button>
									<button
										className={s.cancelBtn}
										onClick={() => setIsEditing(false)}
									>
										Отмена
									</button>
								</div>
							</div>
						)}
					</div>

					<div className={s.ordersHistory}>
						<h2>История заказов</h2>
						{!orders || orders.length === 0 ? (
							<p className={s.noOrders}>У вас пока нет заказов</p>
						) : (
							<div className={s.ordersList}>
								{orders.map(order => (
									<div key={order.id} className={s.orderItem}>
										<div className={s.orderHeader}>
											<span className={s.orderDate}>
												Заказ от {formatDate(order.date)}
											</span>
											<span className={`${s.orderStatus} ${s[order.status]}`}>
												{getStatusText(order.status)}
											</span>
										</div>
										<div className={s.orderDetails}>
											<div className={s.orderType}>
												Тип:{' '}
												{order.type === 'delivery' ? 'Доставка' : 'Самовывоз'}
											</div>
											<div className={s.orderTotal}>
												Сумма: <strong>{order.total} руб</strong>
											</div>
											<div className={s.orderItems}>
												Товары:{' '}
												{order.items
													.map(item => `${item.name} (${item.quantity} шт)`)
													.join(', ')}
											</div>
											{order.address && (
												<div className={s.orderAddress}>
													Адрес: {order.address}
												</div>
											)}
											{order.pickupPoint && (
												<div className={s.orderPickup}>
													Точка самовывоза: {order.pickupPoint}
												</div>
											)}
										</div>
									</div>
								))}
							</div>
						)}
					</div>
				</div>
			</div>
		</div>
	)
}

export default ProfilePage
