import React, { useState, useEffect } from 'react'
import { userAPI, orderAPI } from '../../../services/api'
import styles from './profilePage.module.scss'

const ProfilePage = ({ currentUser, onUserUpdate }) => {
	const [user, setUser] = useState(null)
	const [orders, setOrders] = useState([])
	const [loading, setLoading] = useState(true)
	const [editing, setEditing] = useState(false)
	const [formData, setFormData] = useState({
		name: '',
		email: '',
		address: '',
	})

	useEffect(() => {
		console.log('ProfilePage currentUser:', currentUser)
		if (currentUser && currentUser.id) {
			fetchUserData()
			fetchUserOrders()
		} else {
			console.log('No currentUser or userId')
			setLoading(false)
		}
	}, [currentUser])

	const fetchUserData = async () => {
		console.log('fetchUserData called with userId:', currentUser.id)
		try {
			const userData = await userAPI.getUser(currentUser.id)
			console.log('User data received:', userData)
			setUser(userData)
			setFormData({
				name: userData.name || '',
				email: userData.email || '',
				address: userData.address || '',
			})
		} catch (error) {
			console.error('Ошибка загрузки данных пользователя:', error)
		} finally {
			setLoading(false)
		}
	}

	const fetchUserOrders = async () => {
		try {
			const ordersData = await orderAPI.getUserOrders(currentUser.id)
			console.log('Orders data received:', ordersData)
			setOrders(ordersData)
		} catch (error) {
			console.error('Ошибка загрузки заказов:', error)
		}
	}

	const handleEditToggle = () => {
		setEditing(!editing)
	}

	const handleInputChange = e => {
		const { name, value } = e.target
		setFormData(prev => ({
			...prev,
			[name]: value,
		}))
	}

	const handleSave = async () => {
		try {
			const updatedUser = await userAPI.updateUser(currentUser.id, formData)
			console.log('Updated user:', updatedUser)
			setUser(updatedUser)

			// Обновляем currentUser в App.js
			if (onUserUpdate) {
				onUserUpdate(updatedUser)
			}

			setEditing(false)
			alert('Данные успешно обновлены!')
		} catch (error) {
			console.error('Ошибка обновления данных:', error)
			alert('Ошибка при обновлении данных')
		}
	}

	const formatDate = dateString => {
		return new Date(dateString).toLocaleDateString('ru-RU', {
			day: '2-digit',
			month: '2-digit',
			year: 'numeric',
			hour: '2-digit',
			minute: '2-digit',
		})
	}

	const getStatusText = status => {
		const statusMap = {
			processing: 'В обработке',
			cooking: 'Готовится',
			ready: 'Готов к выдаче',
			delivering: 'Доставляется',
			completed: 'Завершен',
			cancelled: 'Отменен',
		}
		return statusMap[status] || status
	}

	const getStatusClass = status => {
		const statusClassMap = {
			processing: styles.statusProcessing,
			cooking: styles.statusCooking,
			ready: styles.statusReady,
			delivering: styles.statusDelivering,
			completed: styles.statusCompleted,
			cancelled: styles.statusCancelled,
		}
		return statusClassMap[status] || styles.statusDefault
	}

	if (loading) {
		return <div className={styles.loading}>Загрузка профиля...</div>
	}

	if (!user) {
		return <div className={styles.error}>Пользователь не найден</div>
	}

	return (
		<div className={styles.container}>
			<div className={styles.profileSection}>
				<h2>Мой профиль</h2>

				<div className={styles.userInfo}>
					<div className={styles.field}>
						<label>Телефон:</label>
						<span className={styles.phone}>{user.phone}</span>
					</div>

					<div className={styles.field}>
						<label>Имя:</label>
						{editing ? (
							<input
								type='text'
								name='name'
								value={formData.name}
								onChange={handleInputChange}
								className={styles.input}
								placeholder='Введите ваше имя'
							/>
						) : (
							<span>{user.name || 'Не указано'}</span>
						)}
					</div>

					<div className={styles.field}>
						<label>Email:</label>
						{editing ? (
							<input
								type='email'
								name='email'
								value={formData.email}
								onChange={handleInputChange}
								className={styles.input}
								placeholder='Введите ваш email'
							/>
						) : (
							<span>{user.email || 'Не указан'}</span>
						)}
					</div>

					<div className={styles.field}>
						<label>Адрес:</label>
						{editing ? (
							<textarea
								name='address'
								value={formData.address}
								onChange={handleInputChange}
								className={styles.textarea}
								placeholder='Введите ваш адрес'
								rows='3'
							/>
						) : (
							<span>{user.address || 'Не указан'}</span>
						)}
					</div>

					<div className={styles.actions}>
						{editing ? (
							<>
								<button onClick={handleSave} className={styles.saveBtn}>
									Сохранить
								</button>
								<button onClick={handleEditToggle} className={styles.cancelBtn}>
									Отмена
								</button>
							</>
						) : (
							<button onClick={handleEditToggle} className={styles.editBtn}>
								Редактировать профиль
							</button>
						)}
					</div>
				</div>
			</div>

			<div className={styles.ordersSection}>
				<h2>История заказов</h2>

				{orders.length === 0 ? (
					<div className={styles.noOrders}>
						<p>У вас еще нет заказов</p>
					</div>
				) : (
					<div className={styles.ordersList}>
						{orders.map(order => (
							<div key={order.id} className={styles.orderCard}>
								<div className={styles.orderHeader}>
									<div className={styles.orderInfo}>
										<span className={styles.orderNumber}>
											Заказ #{order.orderNumber}
										</span>
										<span className={styles.orderDate}>
											{formatDate(order.date)}
										</span>
									</div>
									<div className={styles.orderMeta}>
										<span
											className={`${styles.status} ${getStatusClass(
												order.status
											)}`}
										>
											{getStatusText(order.status)}
										</span>
										<span className={styles.total}>{order.total} ₽</span>
									</div>
								</div>

								<div className={styles.orderType}>
									Тип: {order.type === 'delivery' ? 'Доставка' : 'Самовывоз'}
								</div>

								<div className={styles.orderItems}>
									<h4>Состав заказа:</h4>
									{order.items &&
										order.items.map((item, index) => (
											<div key={index} className={styles.orderItem}>
												<span className={styles.itemName}>{item.name}</span>
												<span className={styles.itemQuantity}>
													×{item.quantity}
												</span>
												<span className={styles.itemPrice}>
													{item.price * item.quantity} ₽
												</span>
											</div>
										))}
								</div>

								{order.deliveryData && order.type === 'delivery' && (
									<div className={styles.deliveryInfo}>
										<h4>Адрес доставки:</h4>
										<p>{order.deliveryData.address}</p>
										{order.deliveryData.comment && (
											<p className={styles.comment}>
												Комментарий: {order.deliveryData.comment}
											</p>
										)}
									</div>
								)}

								{order.pickupPoint && order.type === 'pickup' && (
									<div className={styles.pickupInfo}>
										<h4>Пункт выдачи:</h4>
										<p>{order.pickupPoint.address}</p>
										{order.pickupPoint.workingHours && (
											<p className={styles.workingHours}>
												Часы работы: {order.pickupPoint.workingHours}
											</p>
										)}
									</div>
								)}
							</div>
						))}
					</div>
				)}
			</div>
		</div>
	)
}

export default ProfilePage
