import React, { useState, useEffect } from 'react'
import { userAPI, orderAPI } from '../../../services/api'
import styles from './profilePage.module.scss'

const ProfilePage = ({ currentUser, onUserUpdate }) => {
	const [user, setUser] = useState(null)
	const [orders, setOrders] = useState([])
	const [loading, setLoading] = useState(true)
	const [editing, setEditing] = useState(false)
	const [formData, setFormData] = useState({
		name: '',
		email: '',
		address: '',
	})

	// –í—Ä–µ–º–µ–Ω–Ω—ã–µ –∏–Ω—Ç–µ—Ä–≤–∞–ª—ã –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–≥–æ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Å—Ç–∞—Ç—É—Å–æ–≤ (–≤ –º–∏–Ω—É—Ç–∞—Ö)
	const statusTimings = {
		processing: 1,
		cooking: 2,
		delivering: 3,
		completed: 4,
	}

	useEffect(() => {
		console.log('ProfilePage currentUser:', currentUser)
		if (currentUser && currentUser.id) {
			fetchUserData()
			fetchUserOrders()
		} else {
			console.log('No currentUser or userId')
			setLoading(false)
		}
	}, [currentUser])

	// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∫–æ—Ä—Ä–µ–∫—Ü–∏–∏ —á–∞—Å–æ–≤–æ–≥–æ –ø–æ—è—Å–∞
	const sqliteDateToLocal = sqliteDate => {
		if (!sqliteDate) return new Date()

		let date

		if (sqliteDate.includes('T')) {
			date = new Date(sqliteDate)
		} else if (sqliteDate.includes(' ')) {
			date = new Date(sqliteDate.replace(' ', 'T'))
		} else {
			date = new Date(sqliteDate)
		}

		const timezoneOffset = date.getTimezoneOffset() * 60000
		return new Date(date.getTime() + timezoneOffset)
	}

	// –£–ø—Ä–æ—â–µ–Ω–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞—Ç—ã - –ë–ï–ó –∫–æ—Ä—Ä–µ–∫—Ü–∏–∏ —á–∞—Å–æ–≤–æ–≥–æ –ø–æ—è—Å–∞
	const parseOrderDate = dateString => {
		if (!dateString) return new Date()

		console.log('üîç Raw date from API:', dateString)

		try {
			// –î–∞—Ç–∞ –ø—Ä–∏—Ö–æ–¥–∏—Ç –≤ —Ñ–æ—Ä–º–∞—Ç–µ '2025-10-23T23:42:21.000Z' - —ç—Ç–æ —É–∂–µ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–µ –≤—Ä–µ–º—è
			const date = new Date(dateString)
			console.log('üîÑ Parsed date:', date.toString())

			return date
		} catch (error) {
			console.error('‚ùå Error parsing date:', error)
			return new Date()
		}
	}

	// –û–±–Ω–æ–≤–∏—Ç–µ –≤—Å–µ —Ñ—É–Ω–∫—Ü–∏–∏ —Ä–∞—Å—á–µ—Ç–∞
	const calculateCurrentStatus = orderDate => {
		const now = new Date()
		const orderTime = parseOrderDate(orderDate)
		const diffInMinutes = Math.floor((now - orderTime) / (1000 * 60))

		console.log('‚è∞ Time analysis:', {
			originalDate: orderDate,
			parsedDate: orderTime.toString(),
			currentTime: now.toString(),
			diffInMinutes: diffInMinutes,
		})

		if (diffInMinutes >= statusTimings.completed) return 'completed'
		if (diffInMinutes >= statusTimings.delivering) return 'delivering'
		if (diffInMinutes >= statusTimings.cooking) return 'cooking'
		if (diffInMinutes >= statusTimings.processing) return 'processing'

		return 'processing'
	}

	const calculateProgress = orderDate => {
		const now = new Date()
		const orderTime = parseOrderDate(orderDate)
		const diffInMinutes = Math.floor((now - orderTime) / (1000 * 60))

		const totalTime = statusTimings.completed
		const progress = Math.min((diffInMinutes / totalTime) * 100, 100)

		return Math.floor(progress)
	}

	const getCurrentStep = orderDate => {
		const now = new Date()
		const orderTime = parseOrderDate(orderDate)
		const diffInMinutes = Math.floor((now - orderTime) / (1000 * 60))

		if (diffInMinutes >= statusTimings.completed) return 4
		if (diffInMinutes >= statusTimings.delivering) return 3
		if (diffInMinutes >= statusTimings.cooking) return 2
		if (diffInMinutes >= statusTimings.processing) return 1

		return 0
	}

	const fetchUserData = async () => {
		try {
			const userData = await userAPI.getUser(currentUser.id)
			setUser(userData)
			setFormData({
				name: userData.name || '',
				email: userData.email || '',
				address: userData.address || '',
			})
		} catch (error) {
			console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', error)
		} finally {
			setLoading(false)
		}
	}

	const fetchUserOrders = async () => {
		try {
			const ordersData = await orderAPI.getUserOrders(currentUser.id)

			const ordersWithCalculatedStatus = ordersData.map(order => {
				const calculatedStatus = calculateCurrentStatus(order.date)
				const progress = calculateProgress(order.date)
				const currentStep = getCurrentStep(order.date)
				const isActive =
					calculatedStatus !== 'completed' && calculatedStatus !== 'cancelled'

				return {
					...order,
					calculatedStatus,
					progress,
					currentStep,
					isActive,
				}
			})

			setOrders(ordersWithCalculatedStatus)
		} catch (error) {
			console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–∫–∞–∑–æ–≤:', error)
		}
	}

	useEffect(() => {
		const interval = setInterval(() => {
			setOrders(prevOrders =>
				prevOrders.map(order => ({
					...order,
					calculatedStatus: calculateCurrentStatus(order.date),
					progress: calculateProgress(order.date),
					currentStep: getCurrentStep(order.date),
					isActive:
						calculateCurrentStatus(order.date) !== 'completed' &&
						calculateCurrentStatus(order.date) !== 'cancelled',
				}))
			)
		}, 60000)

		return () => clearInterval(interval)
	}, [])

	const handleEditToggle = () => {
		setEditing(!editing)
	}

	const handleInputChange = e => {
		const { name, value } = e.target
		setFormData(prev => ({
			...prev,
			[name]: value,
		}))
	}

	const handleSave = async () => {
		try {
			const updatedUser = await userAPI.updateUser(currentUser.id, formData)
			setUser(updatedUser)
			fetchUserOrders()

			if (onUserUpdate) {
				onUserUpdate(updatedUser)
			}

			setEditing(false)
			alert('–î–∞–Ω–Ω—ã–µ —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω—ã!')
		} catch (error) {
			console.error('–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö:', error)
			alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö')
		}
	}

	const formatDate = dateString => {
		return new Date(dateString).toLocaleDateString('ru-RU', {
			day: '2-digit',
			month: '2-digit',
			year: 'numeric',
			hour: '2-digit',
			minute: '2-digit',
		})
	}

	const getStatusText = status => {
		const statusMap = {
			processing: '–í –æ–±—Ä–∞–±–æ—Ç–∫–µ',
			cooking: '–ì–æ—Ç–æ–≤–∏—Ç—Å—è',
			delivering: '–î–æ—Å—Ç–∞–≤–ª—è–µ—Ç—Å—è',
			completed: '–ó–∞–≤–µ—Ä—à–µ–Ω',
			cancelled: '–û—Ç–º–µ–Ω–µ–Ω',
		}
		return statusMap[status] || status
	}

	const getStatusColor = status => {
		switch (status) {
			case 'processing':
				return { background: '#fff3cd', color: '#856404' }
			case 'cooking':
				return { background: '#cce7ff', color: '#004085' }
			case 'delivering':
				return { background: '#d4edda', color: '#155724' }
			case 'completed':
				return { background: '#d1e7dd', color: '#0f5132' }
			case 'cancelled':
				return { background: '#f8d7da', color: '#721c24' }
			default:
				return { background: '#e2e3e5', color: '#383d41' }
		}
	}

	const getTimePassed = orderDate => {
		const now = new Date()
		const orderTime = sqliteDateToLocal(orderDate)
		const diffInMinutes = Math.floor((now - orderTime) / (1000 * 60))

		if (diffInMinutes < 1) return '—Ç–æ–ª—å–∫–æ —á—Ç–æ'
		if (diffInMinutes === 1) return '1 –º–∏–Ω—É—Ç—É –Ω–∞–∑–∞–¥'
		if (diffInMinutes < 5) return `${diffInMinutes} –º–∏–Ω—É—Ç—ã –Ω–∞–∑–∞–¥`
		if (diffInMinutes < 60) return `${diffInMinutes} –º–∏–Ω—É—Ç –Ω–∞–∑–∞–¥`

		const diffInHours = Math.floor(diffInMinutes / 60)
		if (diffInHours === 1) return '1 —á–∞—Å –Ω–∞–∑–∞–¥'
		if (diffInHours < 5) return `${diffInHours} —á–∞—Å–∞ –Ω–∞–∑–∞–¥`
		return `${diffInHours} —á–∞—Å–æ–≤ –Ω–∞–∑–∞–¥`
	}

	if (loading) {
		return <div className={styles.loading}>–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</div>
	}

	if (!user) {
		return <div className={styles.errorMessage}>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω</div>
	}

	const sortedOrders = [...orders].sort((a, b) => {
		if (a.isActive && !b.isActive) return -1
		if (!a.isActive && b.isActive) return 1
		return new Date(b.date) - new Date(a.date)
	})

	return (
		<div className={styles.profilePage}>
			<div className={styles.container}>
				<h1 className={styles.title}>–ú–æ–π –ø—Ä–æ—Ñ–∏–ª—å</h1>

				<div className={styles.profileContent}>
					<div className={styles.profileInfo}>
						<h2>–õ–∏—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h2>

						{!editing ? (
							<div className={styles.infoDisplay}>
								<div className={styles.infoItem}>
									<strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong>
									<span>{user.phone}</span>
								</div>
								<div className={styles.infoItem}>
									<strong>–ò–º—è:</strong>
									<span>{user.name || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}</span>
								</div>
								<div className={styles.infoItem}>
									<strong>Email:</strong>
									<span>{user.email || '–ù–µ —É–∫–∞–∑–∞–Ω'}</span>
								</div>
								<div className={styles.infoItem}>
									<strong>–ê–¥—Ä–µ—Å:</strong>
									<span>{user.address || '–ù–µ —É–∫–∞–∑–∞–Ω'}</span>
								</div>
								<button
									className={styles.editBtn}
									onClick={() => setEditing(true)}
								>
									–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å
								</button>
							</div>
						) : (
							<div className={styles.infoEdit}>
								<div className={styles.formGroup}>
									<label>–ò–º—è:</label>
									<input
										type='text'
										name='name'
										value={formData.name}
										onChange={handleInputChange}
										placeholder='–í–≤–µ–¥–∏—Ç–µ –≤–∞—à–µ –∏–º—è'
									/>
								</div>
								<div className={styles.formGroup}>
									<label>Email:</label>
									<input
										type='email'
										name='email'
										value={formData.email}
										onChange={handleInputChange}
										placeholder='–í–≤–µ–¥–∏—Ç–µ –≤–∞—à email'
									/>
								</div>
								<div className={styles.formGroup}>
									<label>–ê–¥—Ä–µ—Å:</label>
									<input
										type='text'
										name='address'
										value={formData.address}
										onChange={handleInputChange}
										placeholder='–í–≤–µ–¥–∏—Ç–µ –≤–∞—à –∞–¥—Ä–µ—Å'
									/>
								</div>
								<div className={styles.editActions}>
									<button className={styles.saveBtn} onClick={handleSave}>
										–°–æ—Ö—Ä–∞–Ω–∏—Ç—å
									</button>
									<button
										className={styles.cancelBtn}
										onClick={() => setEditing(false)}
									>
										–û—Ç–º–µ–Ω–∞
									</button>
								</div>
							</div>
						)}
					</div>

					<div className={styles.ordersHistory}>
						<h2>–ò—Å—Ç–æ—Ä–∏—è –∑–∞–∫–∞–∑–æ–≤</h2>

						{sortedOrders.length === 0 ? (
							<div className={styles.noOrders}>
								<p>–£ –≤–∞—Å –µ—â–µ –Ω–µ—Ç –∑–∞–∫–∞–∑–æ–≤</p>
							</div>
						) : (
							<div className={styles.ordersList}>
								{sortedOrders.map(order => {
									const statusColors = getStatusColor(order.calculatedStatus)

									return (
										<div key={order.id} className={styles.orderItem}>
											<div className={styles.orderHeader}>
												<div className={styles.orderInfo}>
													<span className={styles.orderNumber}>
														–ó–∞–∫–∞–∑ #{order.orderNumber}
													</span>
													<span className={styles.orderDate}>
														{formatDate(order.date)}
													</span>
													<span className={styles.orderTime}>
														{getTimePassed(order.date)}
													</span>
