import './App.css'
import Header from './components/header/Header'
import { Routes, Route } from 'react-router-dom'
import ProductList from './components/productList/ProductList'
import CartPage from './components/pages/cartPage/CartPage'
import ProfilePage from './components/pages/profilePage/ProfilePage'
import Footer from './components/footer/Footer'
import { useState, useEffect } from 'react'

function App() {
	const [cartItems, setCartItems] = useState([])

	// Загрузка корзины из localStorage при старте
	useEffect(() => {
		const savedCart = localStorage.getItem('cartItems')
		if (savedCart) {
			setCartItems(JSON.parse(savedCart))
		}
	}, [])

	// Сохранение корзины в localStorage при изменении
	useEffect(() => {
		localStorage.setItem('cartItems', JSON.stringify(cartItems))
	}, [cartItems])

	const handleAddToCart = (product, quantity) => {
		console.log('Добавление в корзину:', product.name, quantity) // Debug log
		setCartItems(prevItems => {
			const existingItem = prevItems.find(item => item.id === product.id)

			if (existingItem) {
				return prevItems.map(item =>
					item.id === product.id
						? { ...item, quantity: item.quantity + quantity }
						: item
				)
			} else {
				return [...prevItems, { ...product, quantity }]
			}
		})
	}

	const handleUpdateQuantity = (productId, newQuantity) => {
		console.log('Обновление количества:', productId, newQuantity) // Debug log
		if (newQuantity <= 0) {
			handleRemoveItem(productId)
			return
		}

		setCartItems(prevItems =>
			prevItems.map(item =>
				item.id === productId ? { ...item, quantity: newQuantity } : item
			)
		)
	}

	const handleRemoveItem = productId => {
		console.log('Удаление товара:', productId) // Debug log
		setCartItems(prevItems => prevItems.filter(item => item.id !== productId))
	}

	const handleClearCart = () => {
		setCartItems([])
	}

	return (
		<div className='wrapper'>
			<Header
				cartItemsCount={cartItems.reduce((sum, item) => sum + item.quantity, 0)}
			/>
			<div className='content'>
				<Routes>
					<Route
						path='/'
						element={<ProductList onAddToCart={handleAddToCart} />}
					/>
					<Route
						path='/cart'
						element={
							<CartPage
								cartItems={cartItems}
								onUpdateQuantity={handleUpdateQuantity}
								onRemoveItem={handleRemoveItem}
								onClearCart={handleClearCart}
							/>
						}
					/>
					<Route path='/profile' element={<ProfilePage />} />
				</Routes>
			</div>
			<Footer />
		</div>
	)
}

export default App
