import React, { useState, useEffect, useRef } from 'react'
import styles from './mapModal.module.scss'

const MapModal = ({ onClose, onOrder, type, currentUser }) => {
	const [selectedAddress, setSelectedAddress] = useState('')
	const [selectedPoint, setSelectedPoint] = useState(null)
	const [comment, setComment] = useState('')
	const [map, setMap] = useState(null)
	const [ymaps, setYmaps] = useState(null)
	const mapRef = useRef(null)

	// –ü—É–Ω–∫—Ç—ã —Å–∞–º–æ–≤—ã–≤–æ–∑–∞
	const pickupPoints = [
		{
			id: 1,
			name: '–†–¶ –ù–õ–û',
			address: '—É–ª. –Ø–∫—É–±–∞ –ö–æ–ª—è—Å–∞, 37',
			coords: [27.5619, 53.9023],
			hours: '–ö—Ä—É–≥–ª–æ—Å—É—Ç–æ—á–Ω–æ',
		},
		{
			id: 2,
			name: '–ú–∞—à–µ—Ä–æ–≤–∞',
			address: '–ø—Ä-—Ç. –ú–∞—à–µ—Ä–æ–≤–∞, 78',
			coords: [27.5767, 53.9094],
			hours: '10:00 - 23:00',
		},
		{
			id: 3,
			name: '–¢–¶ –ì–ª–æ–±–æ',
			address: '—É–ª. –£–º–∞–Ω—Å–∫–∞—è, 54',
			coords: [27.4817, 53.8591],
			hours: '10:00 - 23:00',
		},
		{
			id: 4,
			name: '–¢–¶ –ú–æ–∏–æ',
			address: '–ø—Ä. –ü–∞—Ä—Ç–∏–∑–∞–Ω—Å–∫–∞—è, 150–∞',
			coords: [27.5874, 53.8746],
			hours: '10:00 - 23:00',
		},
		{
			id: 5,
			name: '–ü—Ä–∏—Ç—ã—Ü–∫–æ–≥–æ',
			address: '—É–ª. –ü—Ä–∏—Ç—ã—Ü–∫–æ–≥–æ, 83',
			coords: [27.4829, 53.9068],
			hours: '10:00 - 23:00',
		},
	]

	// –ó–∞–≥—Ä—É–∑–∫–∞ –Ø–Ω–¥–µ–∫—Å –ö–∞—Ä—Ç 3.0
	useEffect(() => {
		const loadYandexMaps = async () => {
			if (window.ymaps3) {
				await initMap()
				return
			}

			const script = document.createElement('script')
			script.src =
				'https://api-maps.yandex.ru/v3/?apikey=7f70e535-61db-467e-ac09-3a2712982a31&lang=ru_RU'
			script.onload = async () => {
				await initMap()
			}
			document.head.appendChild(script)
		}

		const initMap = async () => {
			try {
				await window.ymaps3.ready
				const {
					YMap,
					YMapDefaultSchemeLayer,
					YMapDefaultFeaturesLayer,
					YMapMarker,
				} = window.ymaps3

				setYmaps({
					YMap,
					YMapDefaultSchemeLayer,
					YMapDefaultFeaturesLayer,
					YMapMarker,
				})

				const center =
					type === 'pickup' ? [27.5619, 53.9023] : [27.5615, 53.9045] // –ú–∏–Ω—Å–∫

				const newMap = new YMap(mapRef.current, {
					location: {
						center: center,
						zoom: 11,
					},
				})

				// –î–æ–±–∞–≤–ª—è–µ–º —Å—Ö–µ–º–∞—Ç–∏—á–µ—Å–∫–∏–π —Å–ª–æ–π
				newMap.addChild(new YMapDefaultSchemeLayer())
				newMap.addChild(new YMapDefaultFeaturesLayer())

				// –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Ç–∫–∏ –¥–ª—è –ø—É–Ω–∫—Ç–æ–≤ —Å–∞–º–æ–≤—ã–≤–æ–∑–∞
				if (type === 'pickup') {
					pickupPoints.forEach(point => {
						const markerElement = document.createElement('div')
						markerElement.className = styles.marker
						markerElement.innerHTML = `
              <div class="${styles.markerContent}">
                <div class="${styles.markerIcon}">üìç</div>
              </div>
            `

						markerElement.addEventListener('click', () => {
							setSelectedPoint(point)
							setSelectedAddress(`${point.name}, ${point.address}`)

							// –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –±–∞–ª—É–Ω
							showBalloon(newMap, point)
						})

						const marker = new YMapMarker(
							{
								coordinates: point.coords,
								draggable: false,
							},
							markerElement
						)

						newMap.addChild(marker)
					})
				}

				// –î–ª—è –¥–æ—Å—Ç–∞–≤–∫–∏ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –∫–ª–∏–∫–∏ –ø–æ –∫–∞—Ä—Ç–µ
				if (type === 'delivery') {
					newMap.on('click', event => {
						const coords = event.coordinates

						// –°–æ–∑–¥–∞–µ–º –º–∞—Ä–∫–µ—Ä –¥–ª—è –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –º–µ—Å—Ç–∞
						const markerElement = document.createElement('div')
						markerElement.className = styles.marker
						markerElement.innerHTML = `
              <div class="${styles.markerContent}">
                <div class="${styles.markerIcon}">üè†</div>
              </div>
            `

						const marker = new YMapMarker(
							{
								coordinates: coords,
								draggable: false,
							},
							markerElement
						)

						// –£–¥–∞–ª—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–µ –º–∞—Ä–∫–µ—Ä—ã
						newMap.removeChild(
							newMap.children.find(child => child instanceof YMapMarker)
						)
						newMap.addChild(marker)

						// –ü–æ–ª—É—á–∞–µ–º –∞–¥—Ä–µ—Å —á–µ—Ä–µ–∑ –≥–µ–æ–∫–æ–¥–µ—Ä
						geocodeCoordinates(coords).then(address => {
							setSelectedAddress(address)
						})
					})
				}

				setMap(newMap)
			} catch (error) {
				console.error('–û—à–∏–±–∫–∞ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ –∫–∞—Ä—Ç—ã:', error)
			}
		}

		const showBalloon = (map, point) => {
			// –°–æ–∑–¥–∞–µ–º –±–∞–ª—É–Ω —Å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π –æ –ø—É–Ω–∫—Ç–µ
			const balloonElement = document.createElement('div')
			balloonElement.className = styles.balloon
			balloonElement.innerHTML = `
        <div class="${styles.balloonContent}">
          <h3>${point.name}</h3>
          <p>${point.address}</p>
          <p><strong>–ß–∞—Å—ã —Ä–∞–±–æ—Ç—ã:</strong> ${point.hours}</p>
          <p><strong>–í—Ä–µ–º—è –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è:</strong> 15-20 –º–∏–Ω—É—Ç</p>
        </div>
      `

			const balloon = new window.ymaps3.YMapMarker(
				{
					coordinates: point.coords,
					draggable: false,
				},
				balloonElement
			)

			// –£–¥–∞–ª—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π –±–∞–ª—É–Ω
			map.removeChild(
				map.children.find(
					child => child.element && child.element.className === styles.balloon
				)
			)
			map.addChild(balloon)
		}

		const geocodeCoordinates = async coords => {
			try {
				const response = await fetch(
					`https://geocode-maps.yandex.ru/v1/?apikey=7f70e535-61db-467e-ac09-3a2712982a31&format=json&geocode=${coords[0]},${coords[1]}`
				)
				const data = await response.json()

				if (data.response.GeoObjectCollection.featureMember.length > 0) {
					return data.response.GeoObjectCollection.featureMember[0].GeoObject
						.name
				}
				return '–ê–¥—Ä–µ—Å –Ω–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω'
			} catch (error) {
				console.error('–û—à–∏–±–∫–∞ –≥–µ–æ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è:', error)
				return '–ê–¥—Ä–µ—Å –Ω–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω'
			}
		}

		loadYandexMaps()

		return () => {
			if (map) {
				// –û—á–∏—â–∞–µ–º –∫–∞—Ä—Ç—É –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
				map.destroy()
			}
		}
	}, [type])

	const handleOrder = () => {
		if (!selectedAddress) {
			alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –∞–¥—Ä–µ—Å –∏–ª–∏ –ø—É–Ω–∫—Ç –≤—ã–¥–∞—á–∏')
			return
		}

		if (type === 'pickup') {
			onOrder({
				name: selectedPoint.name,
				address: selectedPoint.address,
				workingHours: selectedPoint.hours,
			})
		} else {
			onOrder({
				address: selectedAddress,
				comment: comment,
				phone: currentUser.phone,
			})
		}
	}

	return (
		<div className={styles.modalOverlay} onClick={onClose}>
			<div className={styles.modalContent} onClick={e => e.stopPropagation()}>
				<div className={styles.modalHeader}>
					<h2>
						{type === 'pickup'
							? '–í—ã–±–µ—Ä–∏—Ç–µ –ø—É–Ω–∫—Ç —Å–∞–º–æ–≤—ã–≤–æ–∑–∞'
							: '–í—ã–±–µ—Ä–∏—Ç–µ –∞–¥—Ä–µ—Å –¥–æ—Å—Ç–∞–≤–∫–∏'}
					</h2>
					<button className={styles.closeBtn} onClick={onClose}>
						√ó
					</button>
				</div>

				<div className={styles.mapContainer}>
					<div ref={mapRef} className={styles.map}></div>
				</div>

				<div className={styles.addressSection}>
					<div className={styles.selectedAddress}>
						<strong>–í—ã–±—Ä–∞–Ω–Ω—ã–π {type === 'pickup' ? '–ø—É–Ω–∫—Ç' : '–∞–¥—Ä–µ—Å'}:</strong>
						<span>{selectedAddress || '–ù–µ –≤—ã–±—Ä–∞–Ω'}</span>
					</div>

					{type === 'delivery' && (
						<div className={styles.commentSection}>
							<label>–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π –¥–ª—è –∫—É—Ä—å–µ—Ä–∞:</label>
							<textarea
								value={comment}
								onChange={e => setComment(e.target.value)}
								placeholder='–ù–∞–ø—Ä–∏–º–µ—Ä: –∫–æ–¥ –¥–æ–º–æ—Ñ–æ–Ω–∞, —ç—Ç–∞–∂ –∏ —Ç.–¥.'
								rows='3'
							/>
						</div>
					)}

					{type === 'pickup' && selectedPoint && (
						<div className={styles.pointInfo}>
							<h4>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø—É–Ω–∫—Ç–µ:</h4>
							<p>
								<strong>–ê–¥—Ä–µ—Å:</strong> {selectedPoint.address}
							</p>
							<p>
								<strong>–ß–∞—Å—ã —Ä–∞–±–æ—Ç—ã:</strong> {selectedPoint.hours}
							</p>
							<p>
								<strong>–í—Ä–µ–º—è –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è:</strong> 15-20 –º–∏–Ω—É—Ç
							</p>
						</div>
					)}
				</div>

				<div className={styles.modalActions}>
					<button
						className={styles.orderBtn}
						onClick={handleOrder}
						disabled={!selectedAddress}
					>
						{type === 'pickup' ? '–ó–∞–±—Ä–∞—Ç—å –æ—Ç—Å—é–¥–∞' : '–ó–∞–∫–∞–∑–∞—Ç—å –¥–æ—Å—Ç–∞–≤–∫—É'}
					</button>
					<button className={styles.cancelBtn} onClick={onClose}>
						–û—Ç–º–µ–Ω–∞
					</button>
				</div>
			</div>
		</div>
	)
}

export default MapModal
