import React, { useState, useEffect, useRef } from 'react'
import {
	YMap,
	YMapDefaultSchemeLayer,
	YMapDefaultFeaturesLayer,
	YMapMarker,
	YMapComponentsProvider,
} from 'ymap3-components'
import styles from './mapModal.module.scss'

const MapModal = ({ onClose, onOrder, type, currentUser }) => {
	const [selectedAddress, setSelectedAddress] = useState('')
	const [selectedPoint, setSelectedPoint] = useState(null)
	const [comment, setComment] = useState('')
	const [mapReady, setMapReady] = useState(false)

	// Референсы для маркеров и балунов
	const markersRef = useRef([])
	const balloonRef = useRef(null)

	// Пункты самовывоза
	const pickupPoints = [
		{
			id: 1,
			name: 'РЦ НЛО',
			address: 'ул. Якуба Коляса, 37',
			coords: [27.5619, 53.9023],
			hours: 'Круглосуточно',
		},
		{
			id: 2,
			name: 'Машерова',
			address: 'пр-т. Машерова, 78',
			coords: [27.5767, 53.9094],
			hours: '10:00 - 23:00',
		},
		{
			id: 3,
			name: 'ТЦ Глобо',
			address: 'ул. Уманская, 54',
			coords: [27.4817, 53.8591],
			hours: '10:00 - 23:00',
		},
		{
			id: 4,
			name: 'ТЦ Моио',
			address: 'пр. Партизанская, 150а',
			coords: [27.5874, 53.8746],
			hours: '10:00 - 23:00',
		},
		{
			id: 5,
			name: 'Притыцкого',
			address: 'ул. Притыцкого, 83',
			coords: [27.4829, 53.9068],
			hours: '10:00 - 23:00',
		},
	]

	const center = type === 'pickup' ? [27.5619, 53.9023] : [27.5615, 53.9045]

	// Обработчик клика по карте для доставки
	const handleMapClick = async event => {
		if (type !== 'delivery') return

		const coords = event.coordinates

		// Получаем адрес через геокодер
		try {
			const address = await geocodeCoordinates(coords)
			setSelectedAddress(address)

			// Обновляем маркер
			markersRef.current = markersRef.current.filter(
				marker => marker.props.id !== 'delivery-marker'
			)

			markersRef.current.push(
				<YMapMarker
					key='delivery-marker'
					id='delivery-marker'
					coordinates={coords}
				>
					<div className={styles.marker}>
						<div className={styles.markerContent}>
							<div className={styles.markerIcon}>🏠</div>
						</div>
					</div>
				</YMapMarker>
			)

			// Форсируем обновление
			setMapReady(prev => !prev)
		} catch (error) {
			console.error('Ошибка геокодирования:', error)
		}
	}

	// Обработчик клика по маркеру пункта самовывоза
	const handlePickupPointClick = point => {
		setSelectedPoint(point)
		setSelectedAddress(`${point.name}, ${point.address}`)
		showBalloon(point)
	}

	// Показать балун с информацией
	const showBalloon = point => {
		balloonRef.current = (
			<YMapMarker key='balloon' coordinates={point.coords}>
				<div className={styles.balloon}>
					<div className={styles.balloonContent}>
						<h3>{point.name}</h3>
						<p>{point.address}</p>
						<p>
							<strong>Часы работы:</strong> {point.hours}
						</p>
						<p>
							<strong>Время приготовления:</strong> 15-20 минут
						</p>
					</div>
				</div>
			</YMapMarker>
		)
		setMapReady(prev => !prev)
	}

	const geocodeCoordinates = async coords => {
		try {
			const response = await fetch(
				`https://geocode-maps.yandex.ru/v1/?apikey=7f70e535-61db-467e-ac09-3a2712982a31&format=json&geocode=${coords[0]},${coords[1]}`
			)
			const data = await response.json()

			if (data.response.GeoObjectCollection.featureMember.length > 0) {
				return data.response.GeoObjectCollection.featureMember[0].GeoObject.name
			}
			return 'Адрес не определен'
		} catch (error) {
			console.error('Ошибка геокодирования:', error)
			return 'Адрес не определен'
		}
	}

	// Инициализация маркеров для пунктов самовывоза
	useEffect(() => {
		if (type === 'pickup') {
			markersRef.current = pickupPoints.map(point => (
				<YMapMarker
					key={point.id}
					coordinates={point.coords}
					onClick={() => handlePickupPointClick(point)}
				>
					<div className={styles.marker}>
						<div className={styles.markerContent}>
							<div className={styles.markerIcon}>📍</div>
						</div>
					</div>
				</YMapMarker>
			))
			setMapReady(true)
		}
	}, [type])

	const handleOrder = () => {
		if (!selectedAddress) {
			alert('Пожалуйста, выберите адрес или пункт выдачи')
			return
		}

		if (type === 'pickup') {
			onOrder({
				name: selectedPoint.name,
				address: selectedPoint.address,
				workingHours: selectedPoint.hours,
			})
		} else {
			onOrder({
				address: selectedAddress,
				comment: comment,
				phone: currentUser.phone,
			})
		}
	}

	return (
		<div className={styles.modalOverlay} onClick={onClose}>
			<div className={styles.modalContent} onClick={e => e.stopPropagation()}>
				<div className={styles.modalHeader}>
					<h2>
						{type === 'pickup'
							? 'Выберите пункт самовывоза'
							: 'Выберите адрес доставки'}
					</h2>
					<button className={styles.closeBtn} onClick={onClose}>
						×
					</button>
				</div>

				<div className={styles.mapContainer}>
					<YMapComponentsProvider apiKey='7f70e535-61db-467e-ac09-3a2712982a31'>
						<YMap
							location={{
								center: center,
								zoom: 11,
							}}
							onClick={handleMapClick}
						>
							<YMapDefaultSchemeLayer />
							<YMapDefaultFeaturesLayer />

							{/* Маркеры пунктов самовывоза */}
							{type === 'pickup' && markersRef.current}

							{/* Маркер доставки */}
							{type === 'delivery' && markersRef.current}

							{/* Балун с информацией */}
							{balloonRef.current}
						</YMap>
					</YMapComponentsProvider>
				</div>

				<div className={styles.addressSection}>
					<div className={styles.selectedAddress}>
						<strong>Выбранный {type === 'pickup' ? 'пункт' : 'адрес'}:</strong>
						<span>{selectedAddress || 'Не выбран'}</span>
					</div>

					{type === 'delivery' && (
						<div className={styles.commentSection}>
							<label>Комментарий для курьера:</label>
							<textarea
								value={comment}
								onChange={e => setComment(e.target.value)}
								placeholder='Например: код домофона, этаж и т.д.'
								rows='3'
							/>
						</div>
					)}

					{type === 'pickup' && selectedPoint && (
						<div className={styles.pointInfo}>
							<h4>Информация о пункте:</h4>
							<p>
								<strong>Адрес:</strong> {selectedPoint.address}
							</p>
							<p>
								<strong>Часы работы:</strong> {selectedPoint.hours}
							</p>
							<p>
								<strong>Время приготовления:</strong> 15-20 минут
							</p>
						</div>
					)}
				</div>

				<div className={styles.modalActions}>
					<button
						className={styles.orderBtn}
						onClick={handleOrder}
						disabled={!selectedAddress}
					>
						{type === 'pickup' ? 'Забрать отсюда' : 'Заказать доставку'}
					</button>
					<button className={styles.cancelBtn} onClick={onClose}>
						Отмена
					</button>
				</div>
			</div>
		</div>
	)
}

export default MapModal
