import React, { useState, useEffect, useRef } from 'react'
import {
	YMap,
	YMapDefaultSchemeLayer,
	YMapDefaultFeaturesLayer,
	YMapMarker,
	YMapComponentsProvider,
} from 'ymap3-components'
import styles from './mapModal.module.scss'

const MapModal = ({ onClose, onOrder, type, currentUser }) => {
	const [selectedAddress, setSelectedAddress] = useState('')
	const [selectedPoint, setSelectedPoint] = useState(null)
	const [comment, setComment] = useState('')
	const [mapReady, setMapReady] = useState(false)

	// –†–µ—Ñ–µ—Ä–µ–Ω—Å—ã –¥–ª—è –º–∞—Ä–∫–µ—Ä–æ–≤ –∏ –±–∞–ª—É–Ω–æ–≤
	const markersRef = useRef([])
	const balloonRef = useRef(null)

	// –ü—É–Ω–∫—Ç—ã —Å–∞–º–æ–≤—ã–≤–æ–∑–∞
	const pickupPoints = [
		{
			id: 1,
			name: '–†–¶ –ù–õ–û',
			address: '—É–ª. –Ø–∫—É–±–∞ –ö–æ–ª—è—Å–∞, 37',
			coords: [27.5619, 53.9023],
			hours: '–ö—Ä—É–≥–ª–æ—Å—É—Ç–æ—á–Ω–æ',
		},
		{
			id: 2,
			name: '–ú–∞—à–µ—Ä–æ–≤–∞',
			address: '–ø—Ä-—Ç. –ú–∞—à–µ—Ä–æ–≤–∞, 78',
			coords: [27.5767, 53.9094],
			hours: '10:00 - 23:00',
		},
		{
			id: 3,
			name: '–¢–¶ –ì–ª–æ–±–æ',
			address: '—É–ª. –£–º–∞–Ω—Å–∫–∞—è, 54',
			coords: [27.4817, 53.8591],
			hours: '10:00 - 23:00',
		},
		{
			id: 4,
			name: '–¢–¶ –ú–æ–∏–æ',
			address: '–ø—Ä. –ü–∞—Ä—Ç–∏–∑–∞–Ω—Å–∫–∞—è, 150–∞',
			coords: [27.5874, 53.8746],
			hours: '10:00 - 23:00',
		},
		{
			id: 5,
			name: '–ü—Ä–∏—Ç—ã—Ü–∫–æ–≥–æ',
			address: '—É–ª. –ü—Ä–∏—Ç—ã—Ü–∫–æ–≥–æ, 83',
			coords: [27.4829, 53.9068],
			hours: '10:00 - 23:00',
		},
	]

	const center = type === 'pickup' ? [27.5619, 53.9023] : [27.5615, 53.9045]

	// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–ª–∏–∫–∞ –ø–æ –∫–∞—Ä—Ç–µ –¥–ª—è –¥–æ—Å—Ç–∞–≤–∫–∏
	const handleMapClick = async event => {
		if (type !== 'delivery') return

		const coords = event.coordinates

		// –ü–æ–ª—É—á–∞–µ–º –∞–¥—Ä–µ—Å —á–µ—Ä–µ–∑ –≥–µ–æ–∫–æ–¥–µ—Ä
		try {
			const address = await geocodeCoordinates(coords)
			setSelectedAddress(address)

			// –û–±–Ω–æ–≤–ª—è–µ–º –º–∞—Ä–∫–µ—Ä
			markersRef.current = markersRef.current.filter(
				marker => marker.props.id !== 'delivery-marker'
			)

			markersRef.current.push(
				<YMapMarker
					key='delivery-marker'
					id='delivery-marker'
					coordinates={coords}
				>
					<div className={styles.marker}>
						<div className={styles.markerContent}>
							<div className={styles.markerIcon}>üè†</div>
						</div>
					</div>
