			// –§–æ—Ä—Å–∏—Ä—É–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ
			setMapReady(prev => !prev)
		} catch (error) {
			console.error('–û—à–∏–±–∫–∞ –≥–µ–æ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è:', error)
		}
	}

	// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–ª–∏–∫–∞ –ø–æ –º–∞—Ä–∫–µ—Ä—É –ø—É–Ω–∫—Ç–∞ —Å–∞–º–æ–≤—ã–≤–æ–∑–∞
	const handlePickupPointClick = point => {
		setSelectedPoint(point)
		setSelectedAddress(`${point.name}, ${point.address}`)
		showBalloon(point)
	}

	// –ü–æ–∫–∞–∑–∞—Ç—å –±–∞–ª—É–Ω —Å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π
	const showBalloon = point => {
		balloonRef.current = (
			<YMapMarker key='balloon' coordinates={point.coords}>
				<div className={styles.balloon}>
					<div className={styles.balloonContent}>
						<h3>{point.name}</h3>
						<p>{point.address}</p>
						<p>
							<strong>–ß–∞—Å—ã —Ä–∞–±–æ—Ç—ã:</strong> {point.hours}
						</p>
						<p>
							<strong>–í—Ä–µ–º—è –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è:</strong> 15-20 –º–∏–Ω—É—Ç
						</p>
					</div>
				</div>
			</YMapMarker>
		)
		setMapReady(prev => !prev)
	}

	const geocodeCoordinates = async coords => {
		try {
			const response = await fetch(
				`https://geocode-maps.yandex.ru/v1/?apikey=7f70e535-61db-467e-ac09-3a2712982a31&format=json&geocode=${coords[0]},${coords[1]}`
			)
			const data = await response.json()

			if (data.response.GeoObjectCollection.featureMember.length > 0) {
				return data.response.GeoObjectCollection.featureMember[0].GeoObject.name
			}
			return '–ê–¥—Ä–µ—Å –Ω–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω'
		} catch (error) {
			console.error('–û—à–∏–±–∫–∞ –≥–µ–æ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è:', error)
			return '–ê–¥—Ä–µ—Å –Ω–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω'
		}
	}

	// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –º–∞—Ä–∫–µ—Ä–æ–≤ –¥–ª—è –ø—É–Ω–∫—Ç–æ–≤ —Å–∞–º–æ–≤—ã–≤–æ–∑–∞
	useEffect(() => {
		if (type === 'pickup') {
			markersRef.current = pickupPoints.map(point => (
				<YMapMarker
					key={point.id}
					coordinates={point.coords}
					onClick={() => handlePickupPointClick(point)}
				>
					<div className={styles.marker}>
						<div className={styles.markerContent}>
							<div className={styles.markerIcon}>üìç</div>
						</div>
					</div>
				</YMapMarker>
			))
			setMapReady(true)
		}
	}, [type])

	const handleOrder = () => {
		if (!selectedAddress) {
			alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –∞–¥—Ä–µ—Å –∏–ª–∏ –ø—É–Ω–∫—Ç –≤—ã–¥–∞—á–∏')
			return
		}

		if (type === 'pickup') {
			onOrder({
				name: selectedPoint.name,
				address: selectedPoint.address,
				workingHours: selectedPoint.hours,
			})
		} else {
			onOrder({
				address: selectedAddress,
				comment: comment,
				phone: currentUser.phone,
			})
		}
	}

	return (
		<div className={styles.modalOverlay} onClick={onClose}>
			<div className={styles.modalContent} onClick={e => e.stopPropagation()}>
				<div className={styles.modalHeader}>
					<h2>
						{type === 'pickup'
							? '–í—ã–±–µ—Ä–∏—Ç–µ –ø—É–Ω–∫—Ç —Å–∞–º–æ–≤—ã–≤–æ–∑–∞'
							: '–í—ã–±–µ—Ä–∏—Ç–µ –∞–¥—Ä–µ—Å –¥–æ—Å—Ç–∞–≤–∫–∏'}
					</h2>
					<button className={styles.closeBtn} onClick={onClose}>
						√ó
					</button>
				</div>

				<div className={styles.mapContainer}>
					<YMapComponentsProvider apiKey='7f70e535-61db-467e-ac09-3a2712982a31'>
						<YMap
							location={{
								center: center,
								zoom: 11,
							}}
							onClick={handleMapClick}
						>
							<YMapDefaultSchemeLayer />
							<YMapDefaultFeaturesLayer />

							{/* –ú–∞—Ä–∫–µ—Ä—ã –ø—É–Ω–∫—Ç–æ–≤ —Å–∞–º–æ–≤—ã–≤–æ–∑–∞ */}
							{type === 'pickup' && markersRef.current}

							{/* –ú–∞—Ä–∫–µ—Ä –¥–æ—Å—Ç–∞–≤–∫–∏ */}
							{type === 'delivery' && markersRef.current}

							{/* –ë–∞–ª—É–Ω —Å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π */}
							{balloonRef.current}
						</YMap>
					</YMapComponentsProvider>
				</div>

				<div className={styles.addressSection}>
					<div className={styles.selectedAddress}>
						<strong>–í—ã–±—Ä–∞–Ω–Ω—ã–π {type === 'pickup' ? '–ø—É–Ω–∫—Ç' : '–∞–¥—Ä–µ—Å'}:</strong>
						<span>{selectedAddress || '–ù–µ –≤—ã–±—Ä–∞–Ω'}</span>
					</div>

					{type === 'delivery' && (
						<div className={styles.commentSection}>
							<label>–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π –¥–ª—è –∫—É—Ä—å–µ—Ä–∞:</label>
							<textarea
								value={comment}
								onChange={e => setComment(e.target.value)}
								placeholder='–ù–∞–ø—Ä–∏–º–µ—Ä: –∫–æ–¥ –¥–æ–º–æ—Ñ–æ–Ω–∞, —ç—Ç–∞–∂ –∏ —Ç.–¥.'
								rows='3'
							/>
						</div>
					)}

					{type === 'pickup' && selectedPoint && (
						<div className={styles.pointInfo}>
							<h4>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø—É–Ω–∫—Ç–µ:</h4>
							<p>
								<strong>–ê–¥—Ä–µ—Å:</strong> {selectedPoint.address}
							</p>
							<p>
								<strong>–ß–∞—Å—ã —Ä–∞–±–æ—Ç—ã:</strong> {selectedPoint.hours}
							</p>
							<p>
								<strong>–í—Ä–µ–º—è –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è:</strong> 15-20 –º–∏–Ω—É—Ç
							</p>
						</div>
					)}
				</div>

				<div className={styles.modalActions}>
					<button
						className={styles.orderBtn}
						onClick={handleOrder}
						disabled={!selectedAddress}
					>
						{type === 'pickup' ? '–ó–∞–±—Ä–∞—Ç—å –æ—Ç—Å—é–¥–∞' : '–ó–∞–∫–∞–∑–∞—Ç—å –¥–æ—Å—Ç–∞–≤–∫—É'}
					</button>
					<button className={styles.cancelBtn} onClick={onClose}>
						–û—Ç–º–µ–Ω–∞
					</button>
				</div>
			</div>
		</div>
	)
}

export default MapModal
