import React, { useState, useEffect, useRef } from 'react'
import styles from './mapModal.module.scss'

const MapModal = ({ onClose, onOrder, type, currentUser }) => {
	const [selectedAddress, setSelectedAddress] = useState('')
	const [selectedPoint, setSelectedPoint] = useState(null)
	const [comment, setComment] = useState('')
	const [map, setMap] = useState(null)
	const [ymaps, setYmaps] = useState(null)
	const mapRef = useRef(null)

	// Пункты самовывоза
	const pickupPoints = [
		{
			id: 1,
			name: 'РЦ НЛО',
			address: 'ул. Якуба Коляса, 37',
			coords: [53.9023, 27.5619],
			hours: 'Круглосуточно',
		},
		{
			id: 2,
			name: 'Машерова',
			address: 'пр-т. Машерова, 78',
			coords: [53.9094, 27.5767],
			hours: '10:00 - 23:00',
		},
		{
			id: 3,
			name: 'ТЦ Глобо',
			address: 'ул. Уманская, 54',
			coords: [53.8591, 27.4817],
			hours: '10:00 - 23:00',
		},
		{
			id: 4,
			name: 'ТЦ Моио',
			address: 'пр. Партизанская, 150а',
			coords: [53.8746, 27.5874],
			hours: '10:00 - 23:00',
		},
		{
			id: 5,
			name: 'Притыцкого',
			address: 'ул. Притыцкого, 83',
			coords: [53.9068, 27.4829],
			hours: '10:00 - 23:00',
		},
	]

	// Загрузка Яндекс Карт
	useEffect(() => {
		const loadYandexMaps = () => {
			if (window.ymaps) {
				window.ymaps.ready(initMap)
				return
			}

			const script = document.createElement('script')
			script.src =
				'https://api-maps.yandex.ru/2.1/?apikey=7f70e535-61db-467e-ac09-3a2712982a31&lang=ru_RU'
			script.onload = () => window.ymaps.ready(initMap)
			document.head.appendChild(script)
		}

		const initMap = () => {
			const ymap = window.ymaps
			setYmaps(ymap)

			const center = type === 'pickup' ? [53.9023, 27.5619] : [53.9045, 27.5615] // Минск

			const newMap = new ymap.Map(mapRef.current, {
				center: center,
				zoom: 11,
				controls: ['zoomControl', 'fullscreenControl'],
			})

			// Добавляем метки для пунктов самовывоза
			if (type === 'pickup') {
				pickupPoints.forEach(point => {
					const placemark = new ymap.Placemark(
						point.coords,
						{
							balloonContent: `
              <div class="${styles.balloon}">
                <h3>${point.name}</h3>
                <p>${point.address}</p>
                <p><strong>Часы работы:</strong> ${point.hours}</p>
                <button class="${styles.selectBtn}" data-id="${point.id}">
                  Выбрать этот пункт
                </button>
              </div>
            `,
						},
						{
							preset: 'islands#blueFoodIcon',
						}
					)

					placemark.events.add('click', () => {
						setSelectedPoint(point)
						setSelectedAddress(`${point.name}, ${point.address}`)
					})

					newMap.geoObjects.add(placemark)
				})
			}

			// Для доставки добавляем поиск и выбор адреса
			if (type === 'delivery') {
				const searchControl = new ymap.control.SearchControl({
					options: {
						provider: 'yandex#search',
						noPlacemark: true,
					},
				})

				newMap.controls.add(searchControl)

				// Обработка выбора адреса из поиска
				searchControl.events.add('resultselect', e => {
					const index = e.get('index')
					searchControl.getResult(index).then(result => {
						const address = result.properties.get('name')
						setSelectedAddress(address)
					})
				})

				// Обработка клика по карте для доставки
				newMap.events.add('click', e => {
					const coords = e.get('coords')

					ymap.geocode(coords).then(res => {
						const firstGeoObject = res.geoObjects.get(0)
						const address = firstGeoObject.getAddressLine()
						setSelectedAddress(address)

						// Удаляем предыдущую метку
						newMap.geoObjects.removeAll()

						// Добавляем новую метку
						const placemark = new ymap.Placemark(
							coords,
							{
								balloonContent: address,
							},
							{
								preset: 'islands#redHomeIcon',
							}
						)

						newMap.geoObjects.add(placemark)
					})
				})
			}

			setMap(newMap)
		}

		loadYandexMaps()

		return () => {
			if (map) {
				map.destroy()
			}
		}
	}, [type])

	const handleOrder = () => {
		if (!selectedAddress) {
			alert('Пожалуйста, выберите адрес или пункт выдачи')
			return
		}

		if (type === 'pickup') {
			onOrder({
				name: selectedPoint.name,
				address: selectedPoint.address,
				workingHours: selectedPoint.hours,
			})
		} else {
			onOrder({
				address: selectedAddress,
				comment: comment,
				phone: currentUser.phone,
			})
		}
	}

	return (
		<div className={styles.modalOverlay} onClick={onClose}>
			<div className={styles.modalContent} onClick={e => e.stopPropagation()}>
				<div className={styles.modalHeader}>
					<h2>
						{type === 'pickup'
							? 'Выберите пункт самовывоза'
							: 'Выберите адрес доставки'}
					</h2>
					<button className={styles.closeBtn} onClick={onClose}>
						×
					</button>
				</div>

				<div className={styles.mapContainer}>
					<div ref={mapRef} className={styles.map}></div>
				</div>

				<div className={styles.addressSection}>
					<div className={styles.selectedAddress}>
						<strong>Выбранный {type === 'pickup' ? 'пункт' : 'адрес'}:</strong>
						<span>{selectedAddress || 'Не выбран'}</span>
					</div>

					{type === 'delivery' && (
						<div className={styles.commentSection}>
							<label>Комментарий для курьера:</label>
							<textarea
								value={comment}
								onChange={e => setComment(e.target.value)}
								placeholder='Например: код домофона, этаж и т.д.'
								rows='3'
							/>
						</div>
					)}

					{type === 'pickup' && selectedPoint && (
						<div className={styles.pointInfo}>
							<h4>Информация о пункте:</h4>
							<p>
								<strong>Адрес:</strong> {selectedPoint.address}
							</p>
							<p>
								<strong>Часы работы:</strong> {selectedPoint.hours}
							</p>
							<p>
								<strong>Время приготовления:</strong> 15-20 минут
							</p>
						</div>
					)}
				</div>

				<div className={styles.modalActions}>
					<button
						className={styles.orderBtn}
						onClick={handleOrder}
						disabled={!selectedAddress}
					>
						{type === 'pickup' ? 'Забрать отсюда' : 'Заказать доставку'}
					</button>
					<button className={styles.cancelBtn} onClick={onClose}>
						Отмена
					</button>
				</div>
			</div>
		</div>
	)
}

export default MapModal
