import React, { useState, useEffect } from 'react'
import s from './profilePage.module.scss'

const ProfilePage = () => {
	const [userData, setUserData] = useState({
		name: '',
		email: '',
		phone: '',
		address: '',
	})
	const [orders, setOrders] = useState([])
	const [isLoading, setIsLoading] = useState(true)
	const [isEditing, setIsEditing] = useState(false)

	useEffect(() => {
		fetchUserData()
		fetchOrders()
	}, [])

	const fetchUserData = async () => {
		try {
			const response = await fetch('https://your-mockapi.io/user/1')
			const data = await response.json()
			setUserData(data)
		} catch (error) {
			console.error('Ошибка загрузки данных пользователя:', error)
			// Заглушка для демо
			setUserData({
				name: 'Иван Иванов',
				email: 'ivan@example.com',
				phone: '+79191234567',
				address: 'ул. Примерная, 123',
			})
		} finally {
			setIsLoading(false)
		}
	}

	const fetchOrders = async () => {
		try {
			const response = await fetch(
				'https://68d662abc2a1754b426a8851.mockapi.io/orders?userId=1'
			)
			const data = await response.json()
			setOrders(data)
		} catch (error) {
			console.error('Ошибка загрузки заказов:', error)
			// Заглушка для демо
			setOrders([
				{
					id: 1,
					date: '2024-01-15',
					total: 1250,
					status: 'completed',
					items: ['Том Ям', 'Пад Тай'],
				},
				{
					id: 2,
					date: '2024-01-10',
					total: 890,
					status: 'delivered',
					items: ['Спринг Роллы'],
				},
			])
		}
	}

	const handleSave = async () => {
		try {
			const response = await fetch('https://your-mockapi.io/user/1', {
				method: 'PUT',
				headers: {
					'Content-Type': 'application/json',
				},
				body: JSON.stringify(userData),
			})

			if (response.ok) {
				setIsEditing(false)
				alert('Данные успешно сохранены!')
			}
		} catch (error) {
			console.error('Ошибка сохранения данных:', error)
			alert('Ошибка при сохранении данных')
		}
	}

	const handleInputChange = e => {
		setUserData({
			...userData,
			[e.target.name]: e.target.value,
		})
	}

	if (isLoading) {
		return <div className={s.loading}>Загрузка...</div>
	}

	return (
		<div className={s.profilePage}>
			<div className={s.container}>
				<h1 className={s.title}>Мой профиль</h1>

				<div className={s.profileContent}>
					<div className={s.profileInfo}>
						<h2>Личная информация</h2>

						{!isEditing ? (
							<div className={s.infoDisplay}>
								<div className={s.infoItem}>
									<strong>Имя:</strong> {userData.name}
								</div>
								<div className={s.infoItem}>
									<strong>Email:</strong> {userData.email}
								</div>
								<div className={s.infoItem}>
									<strong>Телефон:</strong> {userData.phone}
								</div>
								<div className={s.infoItem}>
									<strong>Адрес:</strong> {userData.address}
								</div>
								<button
									className={s.editBtn}
									onClick={() => setIsEditing(true)}
								>
									Редактировать
								</button>
							</div>
						) : (
							<div className={s.infoEdit}>
								<div className={s.formGroup}>
									<label>Имя:</label>
									<input
										type='text'
										name='name'
										value={userData.name}
										onChange={handleInputChange}
									/>
								</div>
								<div className={s.formGroup}>
									<label>Email:</label>
									<input
										type='email'
										name='email'
										value={userData.email}
										onChange={handleInputChange}
									/>
								</div>
								<div className={s.formGroup}>
									<label>Телефон:</label>
									<input
										type='tel'
										name='phone'
										value={userData.phone}
										onChange={handleInputChange}
									/>
								</div>
								<div className={s.formGroup}>
									<label>Адрес:</label>
									<input
										type='text'
										name='address'
										value={userData.address}
										onChange={handleInputChange}
									/>
								</div>
								<div className={s.editActions}>
									<button className={s.saveBtn} onClick={handleSave}>
										Сохранить
									</button>
									<button
										className={s.cancelBtn}
										onClick={() => setIsEditing(false)}
									>
										Отмена
									</button>
								</div>
							</div>
						)}
					</div>

					<div className={s.ordersHistory}>
						<h2>История заказов</h2>
						{orders.length === 0 ? (
							<p>У вас пока нет заказов</p>
						) : (
							<div className={s.ordersList}>
								{orders.map(order => (
									<div key={order.id} className={s.orderItem}>
										<div className={s.orderHeader}>
											<span className={s.orderDate}>
												Заказ от {new Date(order.date).toLocaleDateString()}
											</span>
											<span className={`${s.orderStatus} ${s[order.status]}`}>
												{order.status === 'completed'
													? 'Выполнен'
													: order.status === 'delivered'
													? 'Доставлен'
													: 'В обработке'}
											</span>
										</div>
										<div className={s.orderDetails}>
											<span>Сумма: {order.total} руб</span>
											<span>Товары: {order.items.join(', ')}</span>
										</div>
									</div>
								))}
							</div>
						)}
					</div>
				</div>
			</div>
		</div>
	)
}

export default ProfilePage
